<?php
 defined('BASEPATH') OR exit('No direct script access allowed'); class Admin extends CI_Controller{ public function __construct(){ parent::__construct(); $this->load->model('query','',TRUE); $this->load->helper('basic'); $this->load->library('basic'); $this->basic->load(); $info = $this->query->userinfo()->values; $info = json_decode($info); $this->basic->is_login(TRUE); } public function index(){ $this->load->library('basic'); $data = $this->basic->analyze(); $data['admin_title'] = '后台首页'; $data['server'] = php_uname('s').php_uname('r'); $data['os'] = php_uname(); $data['web'] = $_SERVER["SERVER_SOFTWARE"]; $data['php_version'] = PHP_VERSION; $data['max_size'] = get_cfg_var ("upload_max_filesize")?get_cfg_var ("upload_max_filesize"):"不允许"; $data['imgurl_version'] = file_get_contents(FCPATH.'data/version.txt'); $ext = get_loaded_extensions(); $data['ftp'] = array_search('ftp',$ext) ? '支持' : '不支持！'; $this->load->view('admin/header',$data); $this->load->view('admin/left'); $this->load->view('admin/index'); $this->load->view('admin/footer'); } public function urlup(){ $data['admin_title'] = 'URL上传'; $this->load->view('admin/header',$data); $this->load->view('admin/left'); $this->load->view('admin/urlup'); $this->load->view('admin/footer'); } } ?>